/* global BigInt */

const randojs = require("@nastyox/rando.js");
const rando = randojs.rando;

export const initialState = {
  p: BigInt("0"),
  q: BigInt("0"),
  n: BigInt("0"),
  phi: BigInt("0"),
  e: BigInt("0"),
  d: BigInt("0"),
};

const power = (a, b, x) => {
  [a, b, x] = [BigInt(a), BigInt(b), BigInt(x)];
  let res = BigInt("1");
  a %= x;
  while (b > 0) {
    if (b & 1) res = (res * a) % x;
    b >>= 1;
    a = (a * a) % x;
  }
  return res;
};

const millerTest = (d, x) => {
  [d, x] = [BigInt(d), BigInt(x)];
  const a = 2 + (BigInt(rando(100000000, 10000000000000000000000)) % (x - 4));
  const p = power(a, d, x);
  // eslint-disable-next-line
  if (p == 1 || p == x - 1) return true;

  // eslint-disable-next-line
  while (d != x - 1) {
    p = (p * p) % x;
    d *= 2;
    // eslint-disable-next-line
    if (p == 1) return false;
    // eslint-disable-next-line
    if (p == x - 1) return true;
  }
  return false;
};

const checkPrime = (x) => {
  x = BigInt(x);
  // eslint-disable-next-line
  if (x <= 1 || x == 4) return false;
  if (x <= 3) return true;
  let d = BigInt(x - 1);
  // eslint-disable-next-line
  while (d % 2 == 0) d /= 2;
  for (var i = 0; i < 200000; ++i) {
    if (!millerTest(d, x)) return false;
  }
  return true;
};

const reducer = (state, action) => {
  console.log(action);
  console.log(checkPrime(4));
  switch (action.type) {
    case "SET_P_AND_Q": {
      return { ...state, p: action.item };
    }
    case "REMOVE_FROM_BASKET":
      return { state };
    default:
      return state;
  }
};

export default reducer;
