/* global BigInt */

const randojs = require("@nastyox/rando.js");
const rando = randojs.rando;

export const initialState = {
  p: BigInt("0"),
  q: BigInt("0"),
  n: BigInt("0"),
  phi: BigInt("0"),
  e: BigInt("0"),
  d: BigInt("0"),
};

export const checkPrime = (x) => {
  x = BigInt(x);
  // eslint-disable-next-line
  if (x <= 1 || x == 4) return false;
  if (x <= 3) return true;
  let d = BigInt(x - 1);
  while (d % 2 == 0) d /= 2;
  for (var i = 0; i < 200000; ++i) {
    if (!millerTest(d, x)) return false;
  }
  return true;
};

const reducer = (state, action) => {
  console.log(action);
  console.log(checkPrime(4));
  switch (action.type) {
    case "SET_P_AND_Q": {
      return { ...state, p: action.item };
    }
    case "REMOVE_FROM_BASKET":
      return { state };
    default:
      return state;
  }
};

export default reducer;
