import React, { useState, useCallback } from "react";
import EForm from "./EForm";

import "./Main.css";
import PQForms from "./PQForms";

function Main() {
  const [state, setState] = useState(2);

  const onStateChange = useCallback(
    (x) => {
      setState(x);
    },
    [setState]
  );

  return (
    <main className="main">
      <PQForms onStateChange={onStateChange} />
      {state >= 2 && <EForm onStateChange={onStateChange} />}
    </main>
  );
}

export default Main;
