/* global BigInt */

const randojs = require("@nastyox/rando.js");
const rando = randojs.rando;

export const initialState = {
  p: BigInt("0"),
  q: BigInt("0"),
  n: BigInt("0"),
  phi: BigInt("0"),
  e: BigInt("0"),
  d: BigInt("0"),
};

const power = (a, b, n) => {
  [a, b, n] = [BigInt(a), BigInt(b), BigInt(n)];
  let res = BigInt("1");
  a %= n;
  while (b > 0) {
    if (b & 1) res = (res * a) % n;
    b >>= 1;
    a = (a * a) % n;
  }
  return res;
};

const millerTest = (d, n) => {
  [d, n] = [BigInt(d), BigInt(n)];
  const a = 2 + (BigInt(rando(100000000, 10000000000000000000000)) % (n - 4));
  const p = power(a, d, n);
  // eslint-disable-next-line
  if (p == 1 || p == n - 1) return true;

  // eslint-disable-next-line
  while (d != n - 1) {
    p = (p * p) % n;
    d *= 2;
    // eslint-disable-next-line
    if (p == 1) return false;
    // eslint-disable-next-line
    if (p == n - 1) return true;
  }
  return false;
};

export const checkPrime = (n) => {
  n = BigInt(n);
  // eslint-disable-next-line
  if (n <= 1 || n == 4) return false;
  if (n <= 3) return true;
  let d = n - BigInt(1);
  // eslint-disable-next-line
  while (d % BigInt(2) == 0) d /= BigInt(2);
  for (var i = 0; i < 200000; ++i) {
    if (!millerTest(d, n)) return false;
  }
  return true;
};

const reducer = (state, action) => {
  console.log(action);
  console.log(checkPrime(4));
  switch (action.type) {
    case "SET_P_AND_Q": {
      return { ...state, p: action.item };
    }
    case "REMOVE_FROM_BASKET":
      return { state };
    default:
      return state;
  }
};

export default reducer;
