import React, { useCallback, useRef } from "react";
import Paper from "@material-ui/core/Paper";
import Grid from "@material-ui/core/Grid";
import FormControl from "@material-ui/core/FormControl";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import Button from "@material-ui/core/Button";

import "./EncryptOrDecrypt.css";
import { useStateValue } from "../../StateProvider";

function EncryptOrDecrypt() {
  const [{ d, e, n }, dispatch] = useStateValue();

  const messageInput = useRef();
  const cipherInput = useRef();

  return (
    <Paper elevation={2} className="encryptOrDecrypt__container">
      <Typography variant="h5" gutterBottom>
        The private key, <em>d</em> is: <strong>{d}</strong>.
      </Typography>
      <Typography variant="h5" gutterBottom>
        So, public key pair, <em>(e, n)</em> is:{" "}
        <strong>
          ({e}, {n})
        </strong>
      </Typography>
      <Typography variant="h5" gutterBottom>
        So, private key pair, <em>(d, n)</em> is:{" "}
        <strong>
          ({d}, {n})
        </strong>
      </Typography>
      <div className="encryptOrDecrypt__form">
        <Grid container spacing={3}>
          <Grid item xs={6}>
            <FormControl
              fullWidth
              className="encryptOrDecrypt__formControl"
              variant="outlined"
            >
              <TextField
                id="outlined-p-value"
                type="number"
                inputRef={messageInput}
                multiline={true}
                rows={4}
                rowsMax={4}
                variant="outlined"
                label="Enter a message to encrypt"
              />
            </FormControl>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="body1" gutterBottom>
              Some top secret encrypted message.
            </Typography>
          </Grid>
        </Grid>
        <Grid container spacing={3}>
          <Grid item xs={6}>
            <FormControl
              fullWidth
              className="encryptOrDecrypt__formControl"
              variant="outlined"
            >
              <TextField
                id="outlined-p-value"
                type="number"
                inputRef={cipherInput}
                multiline={true}
                rows={4}
                rowsMax={4}
                variant="outlined"
                label="Enter a message to encrypt"
              />
            </FormControl>
          </Grid>
          <Grid item xs={6}>
            <Typography variant="body1" gutterBottom>
              Reveal the original text message.
            </Typography>
          </Grid>
        </Grid>
      </div>
      <Button
        variant="contained"
        color="primary"
        className="encryptOrDecrypt__button"
      >
        Encrypt
      </Button>
    </Paper>
  );
}

export default EncryptOrDecrypt;
